import React from "react";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../stylesheets/Summary.css";
import { graphUrl } from "../services/api-rest";
import Axios from "axios";

function Summary() {
  const navigate = useNavigate();
  const [content, setContent] = useState(null);
  const [isSmallScreen, setIsSmallScreen] = useState(false);
  const smallScreenClasses = "summary-div card shadow col-xxl-10 col-xl-10 col-lg-10 col-md-11 col-sm-12 col-12 d-sm-none";
  const otherSizeScreenClasses = "summary-div card shadow col-xxl-7 col-xl-8 col-lg-10 col-md-12 col-sm-12 col-12";
  const currentDate = new Date();
  const day = currentDate.getDate().toString().padStart(2, "0");
  const month = (currentDate.getMonth() + 1).toString().padStart(2, "0"); // Los meses empiezan en 0
  const year = currentDate.getFullYear().toString();
  const hour = currentDate.getHours().toString().padStart(2, "0");
  const minutes = currentDate.getMinutes().toString().padStart(2, "0");
  const date = `${day}/${month}/${year} ${hour}:${minutes}`;
  const podotactile = "data:image/png;base64,";
  const blocking = "data:image/png;base64,";
  const rampMissing = "data:image/png;base64,";
  const badConditions =" data:image/png;base64,";
  const crosswalkMissing = " data:image/png;base64,";
  const auditiveAlert = " data:image/png;base64,";

  useEffect(() => {
    if(localStorage.getItem("profile") === "Inconvenientes de visin"){
      navigate("/vision");
    }
  }, []);

  useEffect(() => {
    function handleResize() {
      setIsSmallScreen(window.innerWidth < 576);
    }
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleClickBack = () => {
    navigate("/home");
  };

  const handleClickDownload = () => {
    const summaryElement = document.getElementById('summary');
    const summaryHtml = `
    <html>
      <head>
        <title>Resumen de Situacin</title>
        <style>
          body {
            font-family: 'Courier New', Courier, monospace;
            font-size: large;
          }
        </style>
      </head>
      <body>
        ${summaryElement.innerHTML}
      </body>
    </html>
  `;
    const summaryBlob = new Blob([summaryHtml], { type: 'text/html' });

    const downloadLink = document.createElement('a');
    downloadLink.download = 'Resumen-situacion.html';
    downloadLink.href = window.URL.createObjectURL(summaryBlob);

    document.body.appendChild(downloadLink);
    downloadLink.click();
    document.body.removeChild(downloadLink);
  };

  useEffect(() => {
    const url = graphUrl + "/graph/summary";
    Axios.get(url,
      {
        headers: {
          Authorization: localStorage.getItem("token"),
          "Content-Type": "text/plain"
        }
      })
      .then((response) => {
        setContent(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const summaryStyle = {
    fontFamily: "'Courier New', Courier, monospace",
    fontSize: "large"
  };

  return (
    <div className="summary-container">
      {content !== null ?
        <>
          <div className={isSmallScreen ? smallScreenClasses : otherSizeScreenClasses}>
            <div id="summary" style={summaryStyle} className="summary-content">
              <h1 style={{ textAlign: 'center' }}>RESUMEN DE SITUACIN</h1>
              <p>Ciudad: Crdoba capital<br />
                Barrio: Centro<br />
                Fecha: {date} <br /></p>
              <h3><b>Sendas peatonales</b></h3>
              Cantidad de sendas peatonales abarcadas: <b>{content.total_footpaths}</b>
              <br />
              Cantidad de sendas peatonales con algn problema relevado: <b>{content.total_footpaths_problems}</b>
              <br />
              Porcentaje de sendas peatonales con algn problema relevado: <b>{((content.total_footpaths_problems * 100) / content.total_footpaths).toFixed(2)}%</b>
              <h4 style={{ marginTop: '0.5em' }}>Problemas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={rampMissing} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de sendas peatonales con problemas en las rampas: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_rampMissing}</b></p>
                  <p>Porcentaje de sendas peatonales con problemas en las rampas: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_rampMissing * 100) / content.total_footpaths).toFixed(2)}%</b></p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={blocking} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de sendas peatonales bloqueadas: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_blocking}</b></p>
                  <p>Porcentaje de sendas peatonales bloqueadas: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_blocking * 100) / content.total_footpaths).toFixed(2)}%</b></p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={crosswalkMissing} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de sendas peatonales no demarcadas correctamente: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_crosswalkMissing}</b></p>
                  <p>Porcentaje de sendas peatonales no demarcadas correctamente: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_crosswalkMissing * 100) / content.total_footpaths).toFixed(2)}%</b></p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={badConditions} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de sendas peatonales en malas condiciones: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_badConditions}</b></p>
                  <p>Porcentaje de sendas peatonales en malas condiciones: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_badConditions * 100) / content.total_footpaths).toFixed(2)}%</b></p>
                </div>
              </div>

              <h4 style={{ marginTop: '0.5em' }}>Ventajas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={auditiveAlert} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de sendas peatonales con alerta auditiva: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_auditiveAlert}</b></p>
                  <p>Porcentaje de sendas peatonales con alerta auditiva: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_auditiveAlert * 100) / content.total_footpaths).toFixed(2)}%</b></p>
                </div>
              </div>

              <h3><b>Veredas</b></h3>
              Cantidad de veredas abarcadas: <b>{content.total_sidewalks}</b>
              <br />
              Cantidad de veredas con algn problema relevado: <b>{content.total_sidewalks_problems}</b>
              <br />
              Porcentaje de veredas con algn problema relevado: <b>{((content.total_sidewalks_problems * 100) / content.total_sidewalks).toFixed(2)}%</b>
              <h4 style={{ marginTop: '0.5em' }}>Problemas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={blocking} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de veredas bloqueadas: <b style={{ marginLeft: '0.5em' }}>{content.total_footpaths_blocking}</b></p>
                  <p>Porcentaje de veredas bloqueadas: <b style={{ marginLeft: '0.5em' }}>{((content.total_footpaths_blocking * 100) / content.total_sidewalks).toFixed(2)}%</b></p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={badConditions} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de veredas en mal estado: <b style={{ marginLeft: '0.5em' }}>{content.total_sidewalks_badConditions}</b></p>
                  <p>Porcentaje de veredas en mal estado: <b style={{ marginLeft: '0.5em' }}>{((content.total_sidewalks_badConditions * 100) / content.total_sidewalks).toFixed(2)}%</b></p>
                </div>
              </div>

              <h4 style={{ marginTop: '0.5em' }}>Ventajas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={podotactile} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de veredas con baldosas podotctiles: <b style={{ marginLeft: '0.5em' }}>{content.total_sidewalks_podotactile}</b></p>
                  <p>Porcentaje de veredas con baldosas podotctiles: <b style={{ marginLeft: '0.5em' }}>{((content.total_sidewalks_podotactile * 100) / content.total_sidewalks).toFixed(2)}%</b></p>
                </div>
              </div>

              <br />
              <h3><b>Peatonales</b></h3>
              Cantidad de peatonales abarcadas: <b>{content.total_pedestrians}</b>
              <br />
              Cantidad de peatonales con algn problema relevado: <b>{content.total_pedestrians_problems}</b>
              <br />
              Porcentaje de peatonales con algn problema relevado: <b>{((content.total_pedestrians_problems * 100) / content.total_pedestrians).toFixed(2)}%</b>
              <h4 style={{ marginTop: '0.5em' }}>Problemas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={blocking} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de peatonales bloqueadas: <b style={{ marginLeft: '0.5em' }}>{content.total_pedestrians_blocking}</b></p>
                  <p>Porcentaje de peatonales bloqueadas: <b style={{ marginLeft: '0.5em' }}>{((content.total_pedestrians_blocking * 100) / content.total_pedestrians).toFixed(2)}%</b></p>
                </div>
              </div>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={badConditions} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de peatonales en mal estado: <b style={{ marginLeft: '0.5em' }}>{content.total_pedestrians_badConditions}</b></p>
                  <p>Porcentaje de peatonales en mal estado: <b style={{ marginLeft: '0.5em' }}>{((content.total_pedestrians_badConditions * 100) / content.total_pedestrians).toFixed(2)}%</b></p>
                </div>
              </div>

              <h4 style={{ marginTop: '0.5em' }}>Ventajas:</h4>

              <div style={{ display: 'flex', alignItems: 'center' }}>
                <img src={podotactile} style={{ width: 'auto', height: '4em', marginTop: "-0.9em" }} />&nbsp;&nbsp;
                <div style={{ display: 'flex', flexDirection: 'column' }}>
                  <p>Cantidad de peatonales con baldosas podotctiles: <b style={{ marginLeft: '0.5em' }}>{content.total_pedestrians_podotactile}</b></p>
                  <p>Porcentaje de peatonales con baldosas podotctiles: <b style={{ marginLeft: '0.5em' }}>{((content.total_pedestrians_podotactile * 100) / content.total_pedestrians).toFixed(2)}%</b></p>
                </div>
              </div>

            </div>
            <button onClick={handleClickDownload} className="btn btn-warning mt-3" id="download-btn">Descargar resumen</button>
            <button type="button" onClick={handleClickBack} className="btn btn-dark mt-3">Volver</button>
          </div>
        </>
        : <div className="shadow loading-div"> Cargando... </div>}
    </div>
  );
}

export default Summary;